import React, { useState } from "react";
// import Cards  from "./Cards";
// import Heading from "./Heading";
import "./index.css";
const App=()=>{
    const [name,setName]=useState("");
     let txt="";
    const inputEvent=(event)=>{
      txt =event.target.value;
    };
const submit=()=>{
    setName(txt);
}

return(
 <>
 
 <h1>hello,{name}</h1>
 <input type="text" placeholder="likho bsdk" onChange={inputEvent} value={name}></input>
 <button onClick={submit}>click karo</button>

      
 </>
 )
}
export default App;
