import React from "react";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import AddTaskIcon from '@mui/icons-material/AddTask';
const InputBar = () => {

    const event= () => {
        document.getElementsById("dis_1none").style.display = "block";
        document.getElementsById("dis_2none").style.display = "block";
    }
    const even = () => {
        document.getElementsById("dis_1none").style.display = "none";
        document.getElementsById("dis_2none").style.display = "none";
    }
// input-group
    return (<>
        <div className="container upperwala">
            <div className=" upperwala2">
                <div id="dis_1none" className="mb-2 ">
                    <input style={{ border: "none" }} type="text" className="form-control forDisplay w-100" placeholder="Title" aria-label="Recipient's username" aria-describedby="button-addon2" />
                </div>
                <div className="mb-3">
                    <input style={{ border: "none" }} onClick={event} className="form-control" id="exampleFormControlInput1" placeholder="Take a Note" />
                </div>
                <div id="dis_2none" className="mb-3 ">
                    <label className="form-label d-flex mt-2 "><AddTaskIcon onClick={even} /></label>
                </div>
            </div>
        </div>

    </>)
}
export default InputBar;
