import React, { useState } from "react";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import AddTaskIcon from '@mui/icons-material/AddTask';
const InputBar = (props) => {

    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    const event1 = (p) => {

        document.getElementById("dis_1none").style.display = "block"
        document.getElementById("dis_2none").style.display = "block"

    }
    const event2 = (p) => {
        document.getElementById("dis_1none").style.display = "none"
        document.getElementById("dis_2none").style.display = "none"

         props.passNote(note);
         setNote({
            title: "",
            content: ""
        })

    }

    const inputevent = (event) => {

        const  {value,name}=event.target;
      setNote((prevdata)=>{
        return{
            ...prevdata,
            [name]:value,
        }
      })
    }
    // input-group
    return (<>
        <div className="container upperwala">
            <div className=" upperwala2">
                <div id="dis_1none" className="mb-2 ">
                    <input name="title" onChange={inputevent} value={note.title} style={{ border: "none" }} type="text" className="form-control forDisplay w-100" placeholder="Title" aria-label="Recipient's username" aria-describedby="button-addon2" />
                </div>
                <div className="mb-3">
                    <input name="content" onChange={inputevent} value={note.content} style={{ border: "none" }} onClick={event1} className="form-control" id="exampleFormControlInput1" placeholder="Take a Note" />
                </div>
                <div id="dis_2none" className="mb-3 ">
                    <label className="form-label d-flex mt-2 " onClick={event2}><AddTaskIcon /></label>
                </div>
            </div>
        </div>

    </>)
}
export default InputBar;
